pragma solidity ^0.5.0;

contract SupplyChain {

    struct Details {
        uint role;
        uint inventory;
        uint orderReceived;
        uint orderSent;
        address upstream;
        address downstream;
    }

    // Store accounts that have voted
    mapping(address => Details) public players;

    uint[4] public inventory;

    // voted event
    event orderPlaced (
        uint indexed _orderAmount
    );

    event orderSent (
        uint indexed _amt
    );

    constructor () public {
        setRoles();
    }


    function setRoles() private {
        players[0xD1Fc4e50C387D71Ca06cA7f121a3681D649303d5] = Details(1, 100, 300, 0, 0x5a1F364C20E58aB62E61bbCB001061fc56396A78, 0xD1Fc4e50C387D71Ca06cA7f121a3681D649303d5);
        players[0x5a1F364C20E58aB62E61bbCB001061fc56396A78] = Details(2, 20, 15, 0, 0xC570ee4e9dFc403f8618147e6D6836d39774C732, 0xD1Fc4e50C387D71Ca06cA7f121a3681D649303d5);
        players[0xC570ee4e9dFc403f8618147e6D6836d39774C732] = Details(3, 30, 25, 0, 0xb301540f9243eA80A2f3F47eb6839CF4E4341a23, 0x5a1F364C20E58aB62E61bbCB001061fc56396A78);
        players[0xb301540f9243eA80A2f3F47eb6839CF4E4341a23] = Details(4, 40, 35, 0, 0xb301540f9243eA80A2f3F47eb6839CF4E4341a23, 0xC570ee4e9dFc403f8618147e6D6836d39774C732);

        inventory = [100, 20, 30, 40];
    }

    function orderUp(uint _amt) public {
       address upAddreess = players[msg.sender].upstream;
       players[upAddreess].orderReceived += _amt;
 //       emit orderPlaced(_orderAmount);
    }

    function fillStock(uint _amt) public {
        players[msg.sender].inventory += _amt;
        inventory[players[msg.sender].role - 1] += _amt;
        emit orderSent(_amt);
    }

    function clearStock(uint _amt) public {
        require(_amt <= players[msg.sender].inventory && _amt<= players[msg.sender].orderReceived);
        players[msg.sender].inventory -= _amt;
        inventory[players[msg.sender].role-1] -= _amt;

        players[msg.sender].orderReceived -= _amt;
        emit orderSent(_amt);
    }

    function orderDown(uint _amt) public {

        require(_amt <= players[msg.sender].inventory && _amt<= players[msg.sender].orderReceived);

        address downAddress = players[msg.sender].downstream;
        players[downAddress].inventory += _amt;
        inventory[players[downAddress].role-1] += _amt;

        players[msg.sender].inventory -= _amt;
        inventory[players[msg.sender].role-1] -= _amt;

        players[msg.sender].orderReceived -= _amt;

        emit orderSent(_amt);
    }

}
